import React from 'react';
import "./Topbuttons.css";

export class topbuttons extends React.Component{
    constructor(props){
        super();
        this.state = {
            b1 : true,
            b2 : false,
            backcolorb1: 'purple',
            backcolorb2: 'white',
        }
    }

    b1IsClick(){
        if(!this.state.b1){
            this.state.b1 = true;
            this.state.b2 = false;
            this.state.backcolorb1 = 'purple';
            this.state.backcolorb2 = 'gray';
        }
    }

    b2IsClick(){
        if(this.state.b2){
            this.state.b1 = false;
            this.state.b2 = true;
            this.state.backcolorb1 = 'gray';
            this.state.backcolorb2 = 'purple';
        }
    }

    render(){
        return(
            <div>
                <hr/>
                <button className = "btn btn-primary">novo</button>
            </div>
        )
    }

}

export default topbuttons;
