    constructor(props){
        super();
        this.state = {
            backcolorb1: 'purple',
            backcolorb2: 'gray',
        }
    }

    b1IsClick(){
        this.setState({backcolorb1 : ' purple'});
        this.setState({backcolorb2 : 'gray'});

    }

