import React from 'react';
import "./Topbuttons.css";

export class topbuttons extends React.Component{
    constructor(props){
        super();
        this.state = {
            backcolorb1: 'purple',
            backcolorb2: 'gray',
        }
    }

    b1IsClick(){
        if(this.props.inNew){
            this.setState({backcolorb1 : ' purple'});
            this.setState({backcolorb2 : 'gray'});
        }
        else{
            this.setState({backcolorb1 : ' gray'});
            this.setState({backcolorb2 : 'purple'});
        };
    }


    render(){
        return(
            <div>
                <button onClick = {this.b1IsClick.bind(this)} className = "btn btn-primary"  style ={{background:this.state.backcolorb1}}>novo</button>
                <button onClick = {this.b1IsClick.bind(this)} onClick = {this.setState({inNew:false})} className = "btn btn-primary"  style ={{background:this.state.backcolorb2}}>hist√≥rico</button>
            </div>
        )
    }

}

export default topbuttons;
